import React from 'react'
// import Container from 'react-bootstrap/Container'
// import Row from 'react-bootstrap/Row';
// import Card from 'react-bootstrap/Card';
import axios from "axios";
import {
      Card, Button, CardHeader, CardFooter, CardBody, CardText, CardTitle
} from 'reactstrap';
import "./park.css"

function Park(props) {


      const {location, numberDucks, time, numberFoods, food, id } = props

      const handleDelete = () => {
            axios.delete(`/park/${id}`)
                  .then(res => {
                        let response = res.data;
                        return response
                  })
                  .then(data => {

                        if (data.success)
                              alert(`Location ${location} successfully deleted! Please refresh the page !`)
                        else
                              alert('Sorry,Network error.');
                  });


      }


      return (
                        <Card style={{ width: '21rem' }} className="park" body outline color="info">
                              <CardHeader>
                                    <h3>
                                          Location: <span className="location">{location}</span>
                                    </h3>
                                    
                                    
                                    </CardHeader>
                                    <CardBody>

                                          <CardTitle  className="d-flex align-items-start flex-column">
                                                <h5>Number of ducks are fed: <span className="numberDucks">{numberDucks}</span> </h5>
                                                <h5>What kind of food: <span className="food">{food}</span></h5>
                                                <h5>How many food: <span className="numberFoods">{numberFoods}</span></h5>
                                          </CardTitle>
                                          <Button onClick = {handleDelete} >Delete</Button>
                                    </CardBody>
                              <CardFooter className="d-flex align-items-end flex-column">
                                          <p>Date: <span className="date">{time.slice(0,10)}</span></p>
                                          <p>Time: <span className="hour">{time.slice(11,)}</span></p>
                              </CardFooter>
                        </Card>





      )
}

export default React.memo(Park);
