import {
      Card, Button, CardHeader, CardFooter, CardBody, CardText
} from 'reactstrap';

export default function Park(props) {


      const {location, numberDucks, time, numberFoods, food, id } = props

      const handleDelete = () => {

            fetch(`/park?id=${id}`)
      .then(res => {
            res.json()
