// import Container from 'react-bootstrap/Container'
// import Row from 'react-bootstrap/Row';
// import Card from 'react-bootstrap/Card';
import axios from "axios";
import {
      Card, Button, CardHeader, CardFooter, CardBody, CardText, CardTitle
} from 'reactstrap';

export default function Park(props) {


      const {location, numberDucks, time, numberFoods, food, id } = props

      const handleDelete = () => {
            // fetch(`/park`, {
            //       'method': 'DELETE',
            //       headers: {
            //       'Content-type': 'application/json; charset=UTF-8' // Indicates the content
            //       },
            //       body: JSON.stringify({'id': id})
            // })
            //       .then(res => {
            //             let response = res.json();
            //                   console.log("ðŸš€ ~ file: park.jsx ~ line 25 ~ handleDelete ~ response", response)
            //             return response
            //       })
            //       .then(data => {
            //             console.log("ðŸš€ ~ file: park.jsx ~ line 29 ~ handleDelete ~ data", data)
            //             if (data.success)
            //                   alert('Venue successfully deleted!')
            //             else
            //                   alert('Sorry, venue cannot be deleted. Network error.');
            //       });

            const headers = {
                  'Content-type': 'application/json; charset=UTF-8'
            }
            const data = {
                  id: id
            }

            axios.delete('/park', {headers, data})

      }


      return (

                        <Card style={{ width: '21rem' }}>
                              <CardHeader>Location: {location}</CardHeader>
                                    <CardBody>

                                          <CardTitle  className="d-flex align-items-start flex-column">
                                                <h5>Number of ducks are fed: {numberDucks} </h5>
                                                <h5>What kind of food: {food}</h5>
                                                <h5>How many food: {numberFoods}</h5>
                                          </CardTitle>
                                          <Button onClick = {handleDelete} >Delete</Button>
                                    </CardBody>
                              <CardFooter className="d-flex align-items-end flex-column">
                                          <p>Date: {time.slice(0,10)}</p>
                                          <p>Time: {time.slice(11,)}</p>
                              </CardFooter>
                        </Card>





      )
}
